mapper.select:
select b.article_no, b.user_id, b.subject, b.content, b.hit, b.register_time, m.user_name from boards b, members m where b.user_id = m.user_id and b.type = 'U'
and subject like concat('%', #{word}, '%')
and b.${key} = #{word}
order by b.article_no desc limit #{start}, #{spp}

mapper.update:
update boards
set hit = hit + 1
where article_no = ${articleNo}

update boards
set subject = #{subject}, content = #{content}
where article_no = #{articleNo}

mapper.select:
select count(article_no) from boards where type = 'U'
and subject like concat('%', #{word}, '%')
and ${key} = #{word}
